const item = $input.first().json;

const adReply = item?.body?.data?.contextInfo?.externalAdReply || {};

return [
  {
    json: {
      hasSourceId: adReply.sourceId != null,
      hasCtwaClid: adReply.ctwaClid != null,
      sourceId: adReply.sourceId ?? null,
      ctwaClid: adReply.ctwaClid ?? null
    }
  }
];

// Captura o item recebido pelo webhook
const raw = $('input').first().json;
const data = raw.body?.data || {};

// Função para capitalizar nome completo
function formatFullName(name) {
  if (!name) return '';
  const lowerWords = ['de','da','do','das','dos','di','du','e','em','no','na','nos','nas'];
  return name
    .toLowerCase()
    .split(' ')
    .map((word, index) => {
      if (index > 0 && lowerWords.includes(word)) {
        return word;
      }
      return word.charAt(0).toUpperCase() + word.slice(1);
    })
    .join(' ');
}

// Função para extrair primeiro nome com capitalização
function formatFirstName(name) {
  if (!name) return '';
  const first = name.trim().split(' ')[0];
  return first.charAt(0).toUpperCase() + first.slice(1).toLowerCase();
}

// Função para normalizar o número do WhatsApp
function normalizePhone(remoteJid) {
  if (!remoteJid) return '';
  let cleaned = remoteJid.toString().replace(/\D/g, '');
  if (cleaned.startsWith('55') && cleaned.length > 11) {
    cleaned = cleaned.slice(2);
  }
  if (cleaned.length === 11 && cleaned[2] === '9') {
    cleaned = cleaned.slice(0, 2) + cleaned.slice(3);
  }
  return '55' + cleaned;
}

// Segurança: só pegar nome do usuário final quando fromMe for false
let safePushName = '';
if (data.key?.fromMe === false) {
  safePushName = data.pushName || '';
}

// Montagem do resultado final
return [
  {
    json: {
      body: {
        data: {
          user: {
            first_name: formatFirstName(safePushName),
            name: formatFullName(safePushName)
          },
          instance: {
            phone: normalizePhone(data.key?.remoteJid),
            remoteJidAlt: data.key?.remoteJidAlt || null,
            id: data.instanceId || null,
            remoteJid: data.key?.remoteJid || null
          },
          message: {
            id: data.key?.id || null,
            fromMe: data.key?.fromMe || false,
            type: data.messageType || null,
            timestamp: data.messageTimestamp || null,
            conversation: data.message?.conversation || null
          }
        }
      }
    }
  }
];
